mapper.select:
select * from (select q.*,rownum as rnum from (select /*+ index_desc(question question_pk_qno) */ qno, writer, title, write_date writeDate from question
AND WRITER like '%' || #{keyword} || '%'
) q where rownum<=#{endRowNum}) where rnum>=#{startRowNum}

mapper.update:
update notice
title = #{title}, content = #{content},
where noticeno = #{noticeno}

update faq
title = #{title}, content = #{content},
where faqno = #{faqno}
